package fr.declaration.variable;

public class DeclarationApp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte monByte = 1;
		short monShort = 2; 
		int monInt = 3;
		long monLong = 4l;
		float monFloat = 0.5f;
		double monDouble = 0.6;
		char monChar = '7';
		boolean monBoolean = true;
		String monString = "Huit";
		
		System.out.println("Soixante-dix-"+monString);
		
		String randomString = "Voici le résultat d'un calcul : 1+5=6";
		System.out.println(randomString);
		
		randomString = "Voici le résultat d'un calcul :\n1+5=6";
		System.out.println(randomString);
		
	}

}
